import React, { useState } from "react";

export default function Calendar() {
  const date = new Date();
  const [notes, setNotes] = useState({});
  const [eventNote, setEventNote] = useState("");
  const [showNote, setShowNote] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);

  const generateDate = () => {
    const month = date.getMonth();
    const year = date.getFullYear();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    let dates = [];

    for (let i = 1; i <= daysInMonth; i++) {
      dates.push(i);
    }

    return dates;
  };

  const renderDates = () => {
    const dates = generateDate();

    return dates.map((date, index) => (
      <div className="date" key={index} onClick={() => handleDateClick(date)}>
        {date}
        {notes[date] && <span>*</span>}
      </div>
    ));
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setShowNote(true);
  };

  const handleAddEvent = (e) => {
    setEventNote(e.target.value);
  };

  const handleNoteSubmit = () => {
    setNotes({ ...notes, [selectedDate]: eventNote });
    setShowNote(false);
    setEventNote(""); // Reset the note input after submitting
  };

  return (
    <div className="calendar">
      {renderDates()}
      {showNote && (
        <>
          <input type="text" value={eventNote} onChange={handleAddEvent} />
          <button onClick={handleNoteSubmit}>Add Note</button>
        </>
      )}
    </div>
  );
}
