import React, { useState } from "react";
import CalendarHeader from "./CalendarHeader";
import Weekdays from "./Weekdays";
import DaysGrid from "./DaysGrid";
import EventsList from "./EventsList";
import EventPopup from "./EventPopup";
import { getDaysInMonth, isFirstDayOfMonth, isToday } from "./dateUtils";

const CalendarApp = () => {
  const getDaysInMonth = (year, month) =>
    new Date(year, month + 1, 0).getDate();
  const isFirstDayOfMonth = (year, month) => new Date(year, month, 1).getDay();
  const isToday = (date1, date2) =>
    date1.toDateString() === date2.toDateString();
  const currentDate = new Date();
  const [currentMonth, setCurrentMonth] = useState(currentDate.getMonth());
  const [currentYear, setCurrentYear] = useState(currentDate.getFullYear());
  const [selectedDate, setSelectedDate] = useState(currentDate);
  const [showEventPopup, setShowEventPopup] = useState(false);
  const [events, setEvents] = useState([]);
  const [eventDetails, setEventDetails] = useState({ time: "00:00", text: "" });
  const [editingEventId, setEditingEventId] = useState(null);

  const handleDayClick = (day) => {
    const clickedDate = new Date(currentYear, currentMonth, day);
    if (clickedDate >= currentDate || isToday(clickedDate, currentDate)) {
      setSelectedDate(clickedDate);
      setShowEventPopup(true);
      setEventDetails({ ...eventDetails, time: "00:00", text: "" });
      setEditingEventId(null);
    }
  };

  const handleEventSubmit = (newEvent) => {
    let updatedEvents = editingEventId
      ? events.map((event) => (event.id === editingEventId ? newEvent : event))
      : [...events, newEvent];
    updatedEvents.sort((a, b) => new Date(a.date) - new Date(b.date));
    setEvents(updatedEvents);
    setShowEventPopup(false);
  };

  return (
    <div className="calendar-app">
      <CalendarHeader
        currentMonth={currentMonth}
        currentYear={currentYear}
        setCurrentMonth={setCurrentMonth}
        setCurrentYear={setCurrentYear}
      />
      <Weekdays />
      <DaysGrid
        currentMonth={currentMonth}
        currentYear={currentYear}
        daysInMonth={getDaysInMonth(currentYear, currentMonth)}
        firstDayOfMonth={isFirstDayOfMonth(currentYear, currentMonth)}
        handleDayClick={handleDayClick}
      />
      {showEventPopup && (
        <EventPopup
          eventDetails={eventDetails}
          setEventDetails={setEventDetails}
          handleEventSubmit={handleEventSubmit}
          setShowEventPopup={setShowEventPopup}
          editingEventId={editingEventId}
        />
      )}
      <EventsList
        events={events}
        setEvents={setEvents}
        setEditingEventId={setEditingEventId}
      />
    </div>
  );
};

export default CalendarApp;
